(function(global){
    var CANNON = {};
    CANNON.Vec3 = function(x,y,z){ this.x = x||0; this.y = y||0; this.z = z||0; };
    CANNON.Body = function(){ this.position = new CANNON.Vec3(); };
    CANNON.World = function(){ this.bodies = []; };
    CANNON.World.prototype.addBody = function(b){ this.bodies.push(b); };
    CANNON.Ray = function(){};
    CANNON.RaycastResult = function(){};
    CANNON.Body.DYNAMIC = 1;
    global.CANNON = CANNON;
})(window);
